def makelist(seplist, splitter):

    tempword = ""
    madelist = []

    for char in seplist:
        if char == splitter:
            madelist.append(tempword)
            tempword = ""
        elif char == " " and len(tempword) == 0:
            pass
        else:
            tempword += char

    return madelist


