def quiz():
    run = True

    right = 0
    total = 0

    while run:

        wordpos = r.randint(0, len(baselist) - 1)

        badresponse = True
        
        while badresponse:
            
            badresponse = False

            print("\033[96m" + baselist[wordpos], "| Score:", str(right) + "/" + str(total) + "\033[0m")

            action = input("do you know the word?(\"y\" or \"n\", and \"stop\" to quit) ")

            responses = ["y", "n", "stop"]

            if action in responses:

                del baselist[wordpos]
                del defs[wordpos]

                total += 1

                print("\033[91m")

                if action == "y":
                    
                    print("\033[92m")
                    right += 1

                elif action == "stop":
                    run = False

                print(baselist[wordpos] + ":", defs[wordpos])

            else:
                badresponse = True


